import './PokemonDetails.css';

const PokemonDetails = ({ role, pokemon, onAttack }) => {
  console.log(pokemon);
  return (
    <div className="pokemon-details">
      <img src={`${process.env.PUBLIC_URL}/images/sprites/${pokemon.pokedexId}.gif`} alt={pokemon.name} className={role === 'ai' ? 'ai-pokemon' : 'user-pokemon'} />
      <h2>{pokemon.name}</h2>
      <p>HP: {pokemon.stats.life}</p>
      {role === 'user' && (
        <div>
          {pokemon.moves.map((move, index) => (
            <button
              className="pokemon-move-button"
              key={index}
              onClick={() => onAttack(index)}
            >
              {move.name}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default PokemonDetails;
